import React, { useState, useRef, useEffect } from 'react';

const Canvas = () => {
  const canvasRef = useRef(null);
  const [color, setColor] = useState('black');

  let drawing = false;

  const [points, setPoints] = useState({
    x: null,
    y: null,
  });

  // Using the useEffect callback function to grab the canvas after render
  useEffect(() => {
    if (canvasRef.current) {
      const context = canvasRef.current.getContext('2d');
      console.log('Context', context);

      const drawLine = (x0, y0, x1, y1, color, emit) => {
        context.beginPath();
        context.moveTo(x0, y0);
        context.lineTo(x1, y1);
        context.strokeStyle = color;
        context.lineWidth = 2;
        context.stroke();
        context.closePath();
      };

      const mouseDown = (e) => {
        drawing = true;
        const coords = {
          x: e.clientX,
          y: e.clientY,
        };

        setPoints(coords);
      };

      const mouseUp = (e) => {
        // eslint-disable-next-line no-useless-return
        if (!drawing) { return; }
        drawing = false;
        drawLine(points.x, points.y, e.clientX, e.clientY, color, true);
      };

      const mouseMove = (e) => {
        // eslint-disable-next-line no-useless-return
        if (!drawing) { return; }

        const newCoords = {
          x: e.clientX,
          y: e.clientY,
        };
        drawLine(points.x, points.y, { ...newCoords }, color, true);
        setPoints(newCoords);
      };
    }
  });


  console.log('Points', points);
  return (
    <div>
      <canvas
        ref={canvasRef}
        style={{
          border: '1px solid black',
        }}
        onMouseDown={mouseDown}
        onMouseUp={mouseUp}
        onMouseMove={mouseMove}

      />
    </div>
  );
};

export default Canvas;
