import React, { useState, useRef, useEffect } from 'react';
import './Canvas.css';

const Canvas = () => {
  const canvasRef = useRef(null);
  const [context, setContext] = useState(null);
  const [color, setColor] = useState('black');

  let drawing = false;

  const points = {
    x: null,
    y: null,
  };

  // Using the useEffect callback function to grab the canvas after render and setContext
  useEffect(() => {
    if (canvasRef.current) {
      setContext(canvasRef.current.getContext('2d'));
    }
  }, []);

  const drawLine = (x0, y0, x1, y1, color, emit) => {
    console.log(x0, y0, x1, y1, color, context);
    context.beginPath();
    context.moveTo(x0, y0);
    context.lineTo(x1, y1);
    context.strokeStyle = "#F4F4F4";
    context.fillStyle = "#F4F4F4";
    context.lineWidth = 1;
    context.stroke();
    context.closePath();
  };

  const mouseDown = (e) => {
    drawing = true;
    points.x = e.clientX;
    points.y = e.clientY;
  };

  const mouseUp = (e) => {
    // eslint-disable-next-line no-useless-return
    if (!drawing) { return; }
    drawing = false;
    drawLine(points.x, points.y, e.clientX, e.clientY, color, true);
  };

  const mouseMove = (e) => {
    // eslint-disable-next-line no-useless-return
    if (!drawing) { return; }
    drawLine(points.x, points.y, e.clientX, e.clientY, color, true);
    points.x = e.clientX;
    points.y = e.clientY;
  };

  return (
    <div>
      <canvas
        className="whiteboard"
        ref={canvasRef}
        style={{
          border: '1px solid black',
        }}
        onMouseDown={mouseDown}
        onMouseUp={mouseUp}
        onMouseMove={mouseMove}
        onMouseOut={mouseUp}
      />
      <div className="colors">
        <div className="color black"></div>
        <div className="color red"></div>
        <div className="color green"></div>
        <div className="color blue"></div>
        <div className="color yellow"></div>
      </div>
    </div>
  );
};

export default Canvas;
