/* eslint-disable jsx-a11y/no-static-element-interactions */
import React from 'react';
import GoogleMap from 'google-map-react';
import '../Photos/Styling/PopUpBoxStyling.css';
import { mouseDownFunction } from '../Scripts/PopUpBoxScript';
import { GOOGLE_API_KEY } from '../../../../../utils/config';

const mapStyles = {
  width: '100%',
  height: '100%',
  position: 'relative',
};

const markerStyle = {
  height: '45px',
  width: '55px',
  marginTop: '-50px',
};

const imgStyle = {
  height: '100%',
};

const Marker = ({ title }) => (
  <div style={markerStyle}>
    <img style={imgStyle} src="https://res.cloudinary.com/og-tech/image/upload/s--OpSJXuvZ--/v1545236805/map-marker_hfipes.png" alt={title} />
    <p className="googleMapsMarkerLabel">{title}</p>
  </div>
);

const rand = (coord) => {
  const addsub = Math.round(Math.random());
  if (addsub === 1) {
    return coord + (Math.random() / 1000);
  }
  return coord - (Math.random() / 1000);
};


const StalkerMap = ({ locations, center, channel }) => {
  const locationMarkers = locations ? locations.map((user) => (
    <Marker
      className="googleMapsMarker"
      key={`${channel}${Math.random() * 10}`}
      title={`${user.username}`}
      lat={rand(user.location.lat)}
      lng={rand(user.location.lng)}
    />
  )) : <Marker title="default location" {...center} />;

  return (
    <div className="resize-box" onMouseDown={mouseDownFunction}>
      <GoogleMap
        style={{ mapStyles }}
        bootstrapURLKeys={{ key: GOOGLE_API_KEY }}
        center={center}
        zoom={14}
      >
        {locationMarkers}
      </GoogleMap>
    </div>
  );
};

export default StalkerMap;
