import React, { useState, useRef } from 'react';

const Canvas = () => {
  const canvasRef = useRef(null);

  const getContxt = (e) => {
    console.log(canvasRef.current.getContext('2d'))
  }

  return (
    <div>
      <canvas
        ref={canvasRef}
      >
      </canvas>
      <button type="button" onClick={getContxt}>Button</button>
    </div>
  );
};

export default Canvas;
